class Contact {
  static int _nextId = 1;
  static int getNextId() => _nextId++;

  int id;
  String firstName;
  String lastName;
  String address;
  String phone;
  String website;

  Contact.fromMap(Map<String, String> data) {
    id = getNextId();
    firstName = data['First Name'];
    lastName = data['Surname'];
    address = data['DC Office'];
    phone = data['DC Voice'];
    website = data['Web'];
  }

  Contact({
    this.firstName,
    this.lastName,
    this.address,
    this.phone,
    this.website
  }) : id = getNextId();
}

class ContactDB {
  List<Contact> contacts;

  ContactDB(this.contacts);

  ContactDB.fromList(List<Map<String, String>> list) {
    contacts = list.map((data) => new Contact.fromMap(data)).toList();
    contacts.sort((a, b) {
      if (a.lastName != b.lastName)
        return a.lastName.compareTo(b.lastName);
      return a.firstName.compareTo(b.firstName);
    });
  }
}

final ContactDB contactDB = new ContactDB.fromList(_kContacts);
